
public class SubStringJava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String alpha = "I love going to the grocery store";
		
		System.out.println(alpha);
		
		System.out.println(alpha.indexOf("grocery"));//index of grocery starts at 20
		System.out.println(alpha.indexOf("love"));
		System.out.println(alpha.indexOf("ing t"));

	}

}
