
public class SubstringFromEnd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String alpha = "I love to go grocery shopping to get food";
		
		System.out.println(alpha);
		System.out.println(alpha.indexOf("to"));//index of
		System.out.println(alpha.lastIndexOf("to"));//last index of
		
		
		

	}

}
